mapper.select:
SELECT * FROM ( SELECT chalno, chaltitle, chalcontent, chalfilename, memberid FROM challenges where chalstatus = '1'
and ${searchKey} like '%' || #{searchWord} || '%'
ORDER BY chalno DESC ) WHERE rownum <= ${nowPage * onePageRecord}

SELECT * FROM ( SELECT chalno, chaltitle, chalcontent, chalfilename, memberid FROM challenges WHERE chalstatus = '1' AND chalno < ${lastNo}
AND ${searchKey} like '%' || #{searchWord} || '%'
ORDER BY chalno DESC ) WHERE rownum <= 5 ORDER BY chalno DESC

select chalno, memberid, chaltitle, chalcontent,
ParticipantsCnt, chalstartdate, chalenddate, chalFee, chalFileName
from challenges where chalno=${param1}

select chalfilename
from challenges
where chalno=${param1}

select count(*) from
MemberAchievement
where chalno=${param1} and memberid=#{param2}
